import React, {useState, useEffect} from 'react';
import FindCoutries from "./components/FindCountries"
import axios from "axios"
import Countries from './components/Countries';


const App = () => {
    const [ countries, setCountries ] = useState([])
    const [ input, setInput ] = useState("")

    const getCountries = (countryInput) => {
        axios.get('https://restcountries.eu/rest/v2/all')
        .then(response => { 
            setCountries(response.data),
            filterCountries(countryInput)
        })
    }

    const filterCountries = (countryInput) => {
        const filterCountries = countries.filter(country => 
            country.name.toLowerCase().indexOf(countryInput.toLowerCase()) !== -1)
    }

    const handlerInput = (event) => {
        setInput(event.target.value)
        getCountries(input)
    }

    return(

      <div>
          <div>
                <FindCoutries 
                    input={input} 
                    handlerInput={handlerInput}/> 
          </div>
          <div> 

                    <Countries 
                        countries={countries}
                        /> 

          </div>
      </div>
    )
}

export default App;
